import React, { useContext } from "react";
import "./nav.css";
import { Link } from "react-router-dom";
import AuthContext from "../../context/AuthContext";

function menuToggle() {
  const toggleMenu = document.querySelector(".user-auth");
  toggleMenu.classList.toggle("active");
}

export default function Nav() {
  let { user } = useContext(AuthContext);
  return (
    <div>
      <nav>
        <Link to="/HowTo/">
          <h2>HowTo</h2>
        </Link>
        {user && <p>Hello {user.name}</p>}
        <input type="text" placeholder="Search" />
        <div className="nav-end">
          <img src="img/post.png" alt="" />
          <div className="user" onClick={menuToggle}>
            <img class="icon" src="img/user.png" alt="" />
            {user && <p>Hello {user.name}</p>}
          </div>
        </div>
      </nav>
      <div className="spacer"></div>
      <ul className="user-auth">
        <li>
          <Link to="/HowTo/login">Log in</Link>
        </li>
        <li>
          <Link to="/HowTo/signup">Sign Up</Link>
        </li>
      </ul>
    </div>
  );
}
