import axios from "axios";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const ShowPosts = () => {
  const [Posts, setPosts] = useState([]);

  const fetchPosts = async () => {
    const result = await axios.get("http://localhost:8000/api/");

    console.log(result.data);
    setPosts(result.data);
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  const goToDetail = () => {
    alert("detail page");
  };

  return (
    <div className="container">
      <div className="posts-table">
        <div className="table-head">
          <div className="subjects">Posts</div>
          <div className="replies">Replies/Views</div>
          <div className="last-reply">Last Reply</div>
        </div>
        {Posts.filter((post) => post.category === "Coding").map(
          (filteredPost, index) => (
            <div>
              <div className="table-row">
                <Link to={`/${filteredPost.id}`} className="subjects">
                  <div>{filteredPost.title}</div>
                  <p>{filteredPost.description}</p>
                  <span>
                    Started by
                    <b>
                      <a href=""> {filteredPost.owner}</a>
                    </b>
                  </span>
                </Link>
                <div className="replies">
                  3 replies <br></br> 125 views
                </div>
                <div className="last-reply">
                  Nov 24 2021 <br></br> by
                </div>
              </div>
            </div>
          )
        )}
      </div>
    </div>
  );
};
export default ShowPosts;
