import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./posts.css";
import { postData } from "../../data";

export default function Coding() {
  const [filter, setFilter] = useState("");
  const searchText = (event) => {
    setFilter(event.target.value);
  };

  const [posts, setPosts] = useState([]);

  const getPosts = async () => {
    const response = await axios.get("http://localhost:8000/api/");
    setPosts(response.data);
  };

  useEffect(() => {
    getPosts();
  }, []);

  let dataSearch = posts.filter((item) => {
    return Object.keys(item).some((key) =>
      item[key]
        .toString()
        .toLowerCase()
        .includes(filter.toString().toLowerCase())
    );
  });

  return (
    <div className="container">
      <div className="center">
        <input
          type="text"
          placeholder="Search"
          value={filter}
          onChange={searchText.bind(this)}
        />
      </div>
      <div className="posts-table">
        <div className="table-head">
          <div className="subjects">Posts</div>
          <div className="replies">Replies/Views</div>
          <div className="last-reply">Last Reply</div>
        </div>
        {posts
          .filter((post) => post.category === "Coding")
          .map((filteredPost, index) => (
            <div>
              <div className="table-row">
                <Link to="" className="subjects">
                  <div>{filteredPost.title}</div>
                  <p>{filteredPost.description}</p>
                  <span>
                    Started by
                    <b>
                      <a href=""> {filteredPost.owner}</a>
                    </b>
                  </span>
                </Link>
                <div className="replies">
                  3 replies <br></br> 125 views
                </div>
                <div className="last-reply">
                  Nov 24 2021 <br></br> by
                </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}
