import React, { useState, useEffect, Fragment } from "react";
import { Link } from "react-router-dom";
import "./nav.css";

const [loading, setLoading] = useState(true);

useEffect(() => {
  if (localStorage.getItem("token") == null) {
    window.location.replace("http://localhost:3000/login");
  } else {
    setLoading(false);
  }
}, []);

const handleLogout = (e) => {
  e.preventDefault();

  fetch("http://127.0.0.1:8000/api/v1/users/auth/logout/", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: `Token ${localStorage.getItem("token")}`,
    },
  })
    .then((res) => res.json())
    .then((data) => {
      console.log(data);
      localStorage.clear();
      window.location.replace("http://localhost:3000/login");
    });
};

function menuToggle() {
  const toggleMenu = document.querySelector(".user-auth");
  toggleMenu.classList.toggle("active");
}

const Navbar = () => {
  const [isAuth, setIsAuth] = useState(false);

  useEffect(() => {
    if (localStorage.getItem("token") !== null) {
      setIsAuth(true);
    }
  }, []);

  return (
    <div>
      <nav>
        <Link to="/Home">
          <h2>HowTo</h2>
        </Link>
        <input type="text" placeholder="Search" />
        <div className="nav-end">
          <img src="img/post.png" alt="" />
          <div className="user" onClick={menuToggle}>
            <img class="icon" src="img/user.png" alt="" />
            <p>Shelovesssosa</p>
          </div>
        </div>
      </nav>
      <div className="spacer"></div>

      <ul className="user-auth">
        {isAuth === true ? (
          <Fragment>
            {" "}
            <li>
              <Link to="/dashboard">Dashboard</Link>
            </li>
            <li>
              <Link to="/logout">{handleLogout}</Link>
            </li>
          </Fragment>
        ) : (
          <Fragment>
            {" "}
            <li>
              <Link to="/login">Login</Link>
            </li>
            <li>
              <Link to="/signup">Signup</Link>
            </li>
          </Fragment>
        )}
      </ul>
    </div>
  );
};

export default Navbar;
