import cook from "./img/food-delivery.png";
import code from "./img/programming.png";
import sports from "./img/heart-rate.png";
import martial from "./img/martial-arts.png";
import sing from "./img/microphone.png";
import { useState, useEffect } from "react";
import axios from "axios";
import { Counter } from "./pages/categories/categoryCount";

import React from "react";

const Data = () => {
  const [posts, setPosts] = useState([]);

  const getPosts = async () => {
    const response = await axios.get("http://localhost:8000/api/");
    console.log(response.data);
    setPosts(response.data);
  };

  useEffect(() => {
    getPosts();
  }, []);

  let cooking = posts.filter((post) => post.category === "Cook");
  let coding = posts.filter((post) => post.category === "Coding");
  let workout = posts.filter((post) => post.category === "Workout");
  let martialArts = posts.filter((post) => post.category === "Martial Arts");
  let voiceLessons = posts.filter((post) => post.category === "Voice Lessons");

  let cookLength = cooking.length;
  let codingLength = coding.length;
  let workoutLength = workout.length;
  let martialArtsLength = martialArts.length;
  let voiceLessonsLength = voiceLessons.length;

  const data = {
    cardData: [
      {
        CDataories: "Cook",
        Description: "Learn to cook your favorite foods.",
        Image: cook,
        Id: 1,
        postLength: cookLength,
        cook: [
          {
            Title: "How to make macaroni",
            Description: "BEST MACARONI YOU WILL EVER EAT!",
          },
          {
            Title: "How to make the best chicken alfredo.",
            Description: "Creamy chicken alfredo. It will change your life!",
          },
        ],
      },
      {
        Categories: "Coding",
        Description: "Learn how to code from scratch.",
        Image: code,
        Id: 2,
        postLength: codingLength,
      },
      {
        Categories: "Workout",
        Description: "Make yourself stronger than your excuses.",
        Image: sports,
        Id: 3,
        postLength: workoutLength,
      },
      {
        Categories: "Martial Arts",
        Description: "Learn how to become a martial master.",
        Image: martial,
        Id: 4,
        postLength: martialArtsLength,
      },
      {
        Categories: "Voice Lessons",
        Description: "Learn how to sing now.",
        Image: sing,
        Id: 5,
        postLength: voiceLessonsLength,
      },
    ],
  };

  return <div></div>;
};

export default Data;

const data = {
  cardData: [
    {
      CDataories: "Cook",
      Description: "Learn to cook your favorite foods.",
      Image: cook,
      Id: 1,
      postLength: cookLength,
      cook: [
        {
          Title: "How to make macaroni",
          Description: "BEST MACARONI YOU WILL EVER EAT!",
        },
        {
          Title: "How to make the best chicken alfredo.",
          Description: "Creamy chicken alfredo. It will change your life!",
        },
      ],
    },
    {
      Categories: "Coding",
      Description: "Learn how to code from scratch.",
      Image: code,
      Id: 2,
      postLength: codingLength,
    },
    {
      Categories: "Workout",
      Description: "Make yourself stronger than your excuses.",
      Image: sports,
      Id: 3,
      postLength: workoutLength,
    },
    {
      Categories: "Martial Arts",
      Description: "Learn how to become a martial master.",
      Image: martial,
      Id: 4,
      postLength: martialArtsLength,
    },
    {
      Categories: "Voice Lessons",
      Description: "Learn how to sing now.",
      Image: sing,
      Id: 5,
      postLength: voiceLessonsLength,
    },
  ],
};

//# of posts in each category.
